import { TextField, MenuItem, Select, FormControl, InputLabel, Checkbox, FormControlLabel, Button } from '@mui/material';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import './App.css';

function App() {
  // ...

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <div className="container">
        <header className="header">
          <h1>Today, 15-05-2024</h1>
          <div className="day-month-toggle">
            <button className={view === 'day' ? 'active' : ''} onClick={() => setView('day')}>
              Day
            </button>
            <button className={view === 'month' ? 'active' : ''} onClick={() => setView('month')}>
              Month
            </button>
          </div>
        </header>
        <div className="main">
          <div className="calendar">
            {events.map((event, index) => (
              <div key={index} className="event">
                <h4>{event.title}</h4>
                <p>{event.startDate.toLocaleString()} - {event.endDate.toLocaleString()}</p>
                <Button variant="contained" color="error" onClick={() => handleDelete(event.id)}>Delete</Button>
              </div>
            ))}
          </div>
          <div className="todo-form">
            <form>
              {/* ... */}
            </form>
          </div>
        </div>
      </div>
    </LocalizationProvider>
  );
}

export default App;
