import React, { useState } from 'react';
import { TextField, MenuItem, Select, FormControl, InputLabel, Checkbox, FormControlLabel, Button, IconButton, Container, Grid, Typography, Card, CardContent, CardActions } from '@mui/material';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import DeleteIcon from '@mui/icons-material/Delete';
import './App.css';

function App() {
  const [title, setTitle] = useState('');
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [repeat, setRepeat] = useState('');
  const [notificationTime, setNotificationTime] = useState('15 minutes before');
  const [allDay, setAllDay] = useState(false);
  const [view, setView] = useState('day');
  const [events, setEvents] = useState([]);

  const handleSave = () => {
    const newEvent = {
      title,
      startDate,
      endDate,
      repeat,
      notificationTime,
      allDay
    };
    setEvents([...events, newEvent]);
    setTitle('');
    setStartDate(new Date());
    setEndDate(new Date());
    setRepeat('');
    setNotificationTime('15 minutes before');
    setAllDay(false);
  };

  const handleDelete = (index) => {
    setEvents(events.filter((_, i) => i !== index));
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Container maxWidth="lg" className="container">
        <header className="header">
          <Typography variant="h4">Today, 15-05-2024</Typography>
          <div className="day-month-toggle">
            <Button variant={view === 'day' ? 'contained' : 'outlined'} onClick={() => setView('day')}>
              Day
            </Button>
            <Button variant={view === 'month' ? 'contained' : 'outlined'} onClick={() => setView('month')}>
              Month
            </Button>
          </div>
        </header>
        <Grid container spacing={2} className="main">
          <Grid item xs={12} md={8} className="calendar">
            {events.map((event, index) => (
              <Card key={index} className="event">
                <CardContent>
                  <Typography variant="h6">{event.title}</Typography>
                  <Typography>{event.startDate.toLocaleString()} - {event.endDate.toLocaleString()}</Typography>
                </CardContent>
                <CardActions>
                  <IconButton onClick={() => handleDelete(index)} aria-label="delete">
                    <DeleteIcon />
                  </IconButton>
                </CardActions>
              </Card>
            ))}
          </Grid>
          <Grid item xs={12} md={4} className="todo-form">
            <form>
              <TextField
                label="Add a title"
                fullWidth
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                margin="normal"
              />
              <DateTimePicker
                label="Start Date"
                value={startDate}
                onChange={(date) => setStartDate(date)}
                renderInput={(params) => <TextField {...params} fullWidth margin="normal" />}
              />
              <DateTimePicker
                label="End Date"
                value={endDate}
                onChange={(date) => setEndDate(date)}
                renderInput={(params) => <TextField {...params} fullWidth margin="normal" />}
              />
              <FormControl fullWidth margin="normal">
                <InputLabel>Repeat</InputLabel>
                <Select
                  value={repeat}
                  onChange={(e) => setRepeat(e.target.value)}
                >
                  <MenuItem value=""><em>None</em></MenuItem>
                  <MenuItem value="daily">Daily</MenuItem>
                  <MenuItem value="weekly">Weekly</MenuItem>
                  <MenuItem value="monthly">Monthly</MenuItem>
                </Select>
              </FormControl>
              <FormControl fullWidth margin="normal">
                <InputLabel>Notification</InputLabel>
                <Select
                  value={notificationTime}
                  onChange={(e) => setNotificationTime(e.target.value)}
                >
                  <MenuItem value="5 minutes before">5 minutes before</MenuItem>
                  <MenuItem value="10 minutes before">10 minutes before</MenuItem>
                  <MenuItem value="15 minutes before">15 minutes before</MenuItem>
                  <MenuItem value="30 minutes before">30 minutes before</MenuItem>
                  <MenuItem value="1 hour before">1 hour before</MenuItem>
                </Select>
              </FormControl>
              <FormControlLabel
                control={<Checkbox checked={allDay} onChange={(e) => setAllDay(e.target.checked)} />}
                label="All day"
                margin="normal"
              />
              <Button variant="contained" color="primary" onClick={handleSave} style={{ marginTop: '20px' }}>Save</Button>
            </form>
          </Grid>
        </Grid>
      </Container>
    </LocalizationProvider>
  );
}

export default App;
